#define PB push_back
#define MP make_pair
const ll MOD = 1000000009LL;
const ll INF = 1ULL<<60ULL;
const ll UNDEF = -1;
template<typename T> inline bool chkmax(T &a, T b) { return a < b ? a = b, true : false; }
template<typename T> inline bool chkmin(T &a, T b) { return a > b ? a = b, true : false; }
const ll MAXN=2004;
ll d[2][MAXN];
ll w[2][MAXN];
bool o[2][MAXN];
class DrivingPlans {
	public:
	int count(int N, vector <int> A, vector <int> B, vector <int> C) {
		ll n=N;
		{
			ll c=0;
			for (ll x=1;x<=n;x++) {
				d[c][x]=INF;
				w[c][x]=0;
				o[c][x]=false;
			}
			d[c][1]=0;
			w[c][1]=1;
		}
		ll lims=n+2;
		ll bestdist=INF;
		ll bestway=0;
		bool besto=false;
		for (ll s=0;s<lims;s++) {
			ll p=s%2;
			ll c=(s+1)%2;
			for (ll x=1;x<=n;x++) {
				d[c][x]=d[p][x];
				w[c][x]=0;
				o[c][x]=o[p][x];
			}
			ll numedge=A.size();
			for (ll i=0;i<numedge;i++) {
				ll a=A[i],b=B[i],t=C[i];
				for (ll j=0;j<2;j++) {
					ll newdist=d[p][a]+t;
					ll ways=w[p][a];
					ll ovf=(t==0)||o[p][a];
					if (newdist<d[c][b]) {
						w[c][b]=ways;
						d[c][b]=newdist;
						o[c][b]=ovf;
						/*if (o[c][n]) {
							printf("LT:%lld->%lld. t:%lld opa:%d\n",a,b,t,o[p][a]);
						}*/
					}
					else if (newdist==d[c][b]) {
						//if (b==n) printf("a:%lld addways:%lld\n",a,ways);
						w[c][b]+=ways;
						w[c][b]%=MOD;
						o[c][b]|=ovf;
						/*if (o[c][n]) {
							printf("EQ:%lld->%lld. t:%lld opa:%d\n",a,b,t,o[p][a]);
						}*/
					}
					swap(a,b);
				}
			}
			{
				if (d[c][n]<bestdist) {
					bestdist=d[c][n];
					besto=o[c][n];
					bestway=w[c][n];
				}
				else if (d[c][n]==bestdist) {
					besto|=o[c][n];
					bestway+=w[c][n];
					bestway%=MOD;
				}
			}
		}
		if (besto) return -1;
		else return bestway;

	}
};
